mapper.insert:
INSERT INTO board (
    user_no, user_name, board_title, board_content, board_category
) VALUES (
    #{user_no}, #{user_name}, #{board_title}, #{board_content}, #{board_category}
)

mapper.update:
UPDATE board
SET
    board_title = #{board_title},
    board_content = #{board_content},
    board_category = #{board_category}
WHERE board_id = #{board_id}

	UPDATE board
    SET board_status = 'deleted'
    WHERE board_id = #{board_id}
