mapper.select:
SELECT * FROM board
AND board_title LIKE CONCAT('%', #{title}, '%')
AND board_cont LIKE CONCAT('%', #{content}, '%')
AND board_status = #{status}
AND board_category = #{category}
ORDER BY board_id DESC

mapper.insert:
INSERT INTO board (
    user_no, board_title, board_content, board_category, board_status
) VALUES (
    #{user_no}, #{board_title}, #{board_content}, #{board_category}, #{board_status}
)

mapper.update:
	UPDATE board
    SET board_status = 'deleted'
    WHERE board_id = #{board_id}

UPDATE board
SET board_viewcount = board_viewcount + 1
WHERE board_id = #{board_id}
