mapper.insert:
INSERT INTO board_file (
    board_id, file_original_name, file_saved_name,
    file_path, file_size, file_type
) VALUES (
    #{board_id}, #{file_original_name}, #{file_saved_name},
    #{file_path}, #{file_size}, #{file_type}
)

mapper.update:
UPDATE board_file
SET file_status = 'deleted'
WHERE board_id = #{board_id}
