mapper.insert:
INSERT INTO tbl_board (
  title, content, writer, passwd, view_count, created_at
) VALUES (
  #{title}, #{content}, #{writer}, #{passwd}, 0, NOW()
)

mapper.update:
UPDATE tbl_board
SET deleted_at = NOW()
WHERE bno = #{bno}

UPDATE tbl_board
SET view_count = view_count + 1
WHERE bno = #{bno}
