mapper.select:
SELECT club_id
FROM tbl_recruit_board
WHERE bno = #{bno}

SELECT user_no
FROM tbl_recruit_board
WHERE bno = #{bno}

mapper.update:
UPDATE tbl_club_apply
SET status = 'canceled', apply_date = NOW()
WHERE bno = #{bno}
  AND appli_user_no = #{appli_user_no}
  AND status = 'pending'

UPDATE tbl_club_apply
SET status = 'pending', apply_date = NOW()
WHERE bno = #{bno}
  AND appli_user_no = #{appli_user_no}

mapper.select:
SELECT *
FROM tbl_club_apply
WHERE bno = #{bno}
  AND appli_user_no = #{appli_user_no}
ORDER BY apply_date DESC
LIMIT 1

mapper.update:
UPDATE tbl_club_apply
SET status = #{status}
WHERE apply_id = #{apply_id}

mapper.select:
SELECT * FROM tbl_club_apply
WHERE club_id = #{club_id}
ORDER BY apply_date DESC

mapper.update:
UPDATE tbl_club_apply
SET status = 'withdrawn'
WHERE club_id = #{club_id}
  AND appli_user_no = #{appli_user_no}
  AND status = 'approved'

UPDATE tbl_club_apply
SET status = 'kicked'
WHERE club_id = #{club_id}
  AND appli_user_no = #{appli_user_no}
  AND status = 'approved'

mapper.select:
SELECT user_name
