mapper.select:
SELECT * FROM tbl_recruit_board
WHERE club_id = #{clubId}
ORDER BY bno DESC

mapper.update:
UPDATE tbl_recruit_board
SET title = #{title},
    content = #{content}
WHERE bno = #{bno}

mapper.select:
SELECT COUNT(*)
FROM tbl_club
WHERE club_id = #{clubId}
  AND leader_user_id = #{userId}
